<?php

namespace App\Http\Controllers;
use Illuminate\Http\Request;
use Illuminate\Routing\Controllers\HasMiddleware;
use App\Http\Middleware\checkTimeAccess;

class ProductController extends Controller implements HasMiddleware
{
    // add các middleware cho controller
    public static function middleware(): array
    {
        return [
            checkTimeAccess::class,
        ];
    }
    public function index()
    {
        $title = 'Sản phẩm của chúng tôi';
        $products = [
            ['id' => 1, 'name' => 'iPhone 15', 'price' => 25000000, "image" => "data:image/jpeg;base64,", "description" => "iPhone 15 với nhiều tính năng nổi bật."],
            ['id' => 2, 'name' => 'Samsung S245', 'price' => 22000000, "image" => "data:image/jpeg;base64,", "description" => "Samsung S24 với hiệu năng mạnh mẽ."],
        ];
        return view('product.index', compact('products', 'title'));
    }
    public function detail($id){
        $title = "Chi tiết sản phẩm";
        $products = [
            ['id' => 1, 'name' => 'iPhone 15', 'price' => 25000000, "image" => "data:image/jpeg;base64,", "description" => "iPhone 15 với nhiều tính năng nổi bật."],
            ['id' => 2, 'name' => 'Samsung S245', 'price' => 22000000, "image" => "data:image/jpeg;base64,", "description" => "Samsung S24 với hiệu năng mạnh mẽ."],
        ];
        $product = collect($products)->firstWhere('id', $id);
        return view('product.detail', compact('title', 'product'));
    }   
    public function add(){
        $title = "Add Product";
        return view('product.add', compact('title'));
    }
    public function store(Request $request)
    {
        $data = $request->all();
        return view('product.store', compact('data'));
    }
}
